);
CREATE TABLE nw.public.opcion (
	idopcion serial NOT NULL,
	idproyecto integer NOT NULL,
	opcion varchar(100) NOT NULL,
	formulario varchar(200),
	imagen varchar(200),
	idtipopantalla integer,
	habilitar bool DEFAULT true,
