	codigoevolution varchar(100),
	identificacion varchar(20),
	nombres varchar(150),
	apellidos varchar(150),
	tipo integer DEFAULT 1,
	expirar_sesion bool NOT NULL,
	dias_clave_activa integer,
	estado bool NOT NULL,
	PRIMARY KEY (idusuario)
);
